sysevent_script_action.script:
// Get and assign client information for script use, reducing server calls

var mygroups = gs.getUser().getMyGroups();
var mysession = gs.getSession();

session.putClientData('u_userGroupList', mygroups);
session.putClientData('u_jsUserGroupList', jsj2(mygroups));

// set up so the display name for sys_user can be referenced as g_user.userName 

var userid = gs.getUserID();
var grname = new GlideRecord('sys_user');

grname.addQuery('sys_id', userid);
grname.query();

if(grname.next()) {
	session.putClientData('u_name',grname.name); // get users display name
	session.putClientData('sysid', grname.sys_id); // get users sys ID

	session.putClientData('u_departmentname', grname.department); // get users department
}
